class AsymmetricKeyGenerator{
  //final LocalStorage storage = LocalStorage('go_together_app');
  final CustomStorage cStorage = CustomStorage();
  //final box = GetStorage(); // use if localStorage cause issue

  final indexPrivate = "privateKey";
  final indexPublic = "pubKey";
  var id = "1";

  static final AsymmetricKeyGenerator _instance = AsymmetricKeyGenerator._internal();
  factory AsymmetricKeyGenerator() {
    return _instance;
  }
  AsymmetricKeyGenerator._internal();

  setId(String newId){
    id = newId;
  }

  Future<String> getPubKeyFromStorage() async {
    dynamic currentPubkey = await cStorage.get("$indexPublic$id");
    log("pubkey = " + currentPubkey.toString());
    //box.write('quote', 'GetX is the best');
    //log(box.read('quote'));
    if(currentPubkey == null){
      generateKey();
    }
    else{
      return currentPubkey.toString();
    }
    dynamic newPubkey = await cStorage.get("$indexPublic$id");
    log("pubkey generated = " + newPubkey.toString());
    return newPubkey.toString();
  }
  setPubKeyFromStorage(String pubKey) async{
    await cStorage.set("$indexPublic$id", pubKey);
  }
  Future<String> getPrivateKeyFromStorage() async {
    dynamic currentPrivKey = await cStorage.get("$indexPrivate$id");
    log("priv key = " + currentPrivKey.toString());

    if(currentPrivKey == null){
      generateKey();
    }
    else{
      return currentPrivKey.toString();
    }
    dynamic newPrivKey = await cStorage.get("$indexPrivate$id");
    log("pubkey generated = " + newPrivKey.toString());
    return newPrivKey.toString();
  }
  setPrivateKeyFromStorage(String privateKey) async {
    await cStorage.set("$indexPrivate$id", privateKey);
  }

  generateKey({bool isTestMode = false}){
    if(isTestMode){
      if(id=="1") {
        setPubKeyFromStorage("");
        setPrivateKeyFromStorage("");
      }
      else if(id=="2"){
        setPubKeyFromStorage("");
        setPrivateKeyFromStorage("");
      }
      else{
        setPubKeyFromStorage("");
        setPrivateKeyFromStorage("");
      }
    }
    else {
      final pair = generateRSAkeyPair(exampleSecureRandom());
      final public = pair.publicKey;
      final private = pair.privateKey;

      final pemPublicKey = encodePublicKeyToPem(public);
      final pemPrivateKey = encodePrivateKeyToPem(private);
      final pubKey = parsePublicKeyFromPem(pemPublicKey);
      //final privKey = parsePrivateKeyFromPem(pemPrivateKey);

      Function setKeys = ()async {
        await setPubKeyFromStorage(pemPublicKey);
        await setPrivateKeyFromStorage(pemPrivateKey);
      };
      setKeys();

    }
  }
}
